<!-- register Modal --><form class="modal registerForm fade" id="registerModal" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">    <div class="modal-dialog modal-dialog-centered">        <div class="modal-content">            <div class="modal-header">                <h1 class="modal-title fs-5 text-muted" id="staticBackdropLabel">Register</h1>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <div class="modal-body">                <div class="form-floating mb-3">                    <input type="email" class="form-control" id="Email" name="Email" placeholder="name@example.com">                    <label for="Email">Email address</label>                    <div id="Email_msg" class="invalid-feedback"></div>                </div>                <div class="form-floating mb-3">                    <input type="number" class="form-control" name="contactNumber" id="contactNumber">                    <label for="contactNumber">Active Number</label>                    <div id="contactNumber_msg" class="invalid-feedback"></div>                </div>                <div class="form-floating mb-3">                    <input type="text" class="form-control" name="Address" id="Address">                    <label for="Address">Address</label>                    <div id="Address_msg" class="invalid-feedback"></div>                </div>                <div class="form-floating mb-3">                    <input type="text" class="form-control" name="username" id="username">                    <label for="username">username</label>                    <div id="username_msg" class="invalid-feedback"></div>                </div>                <div class="form-floating mb-3">                    <input autocomplete="password" type="password" class="form-control" name="password" id="password">                    <label for="password">password</label>                    <div id="password_msg" class="invalid-feedback"></div>                </div>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>                <button id="btn_register" type="submit" class="btn btn-primary">Sign Up</button>            </div>        </div>    </div></form>