<?phpnamespace controllers;require_once '../models/cart.php';class cartController{  public function store():void  {    $productName = $_POST['productName'];    $total = $_POST['productPrice'];    $pax = $_POST['numberOfPax'];    $image = $_POST['image'];    $id = $_POST['ProductId'];    if (empty($pax)){        echo json_encode(['success'=> false,'message'=>'Please select the number of pax']);        return;    }     session_start();      $cart = new \models\cart();      $clientId = $_SESSION['user'];      if ($cart->checkIfProductIsAlreadyExistInCart($id,$clientId)){          echo json_encode(['success'=> false,'message'=>'Product already exist in cart']);          return;      }        $cart->store($productName, $total, $pax, $image, $id, $clientId);  }  public function show($clientId):void  {      $cart = new \models\cart();     $data = $cart->showToCart($clientId);     $card = '';     if ($data){         foreach ($data as $item){             $card .= '           <!-- card start-->        <div class="card">            <img src="../ProductImageUpload/'.$item['image'].'" alt="image" class="card-img card-img-top">            <div class="card-body">                <h5 class="card-title">'.$item['productName'].'</h5>                <div>                   <label for="quantity">                       Quantity                       <input type="number" min="1" id="quantity" value="1" class="form-control" placeholder="Quantity">                       <input type="hidden" id="total" value="'.$item['total'].'">                        <input type="hidden" id="productName" value="'.$item['productName'].'">                   </label>                    <button id="removeItem_btn" type="button" value="'.$item['id'].'" class="btn badge bg-danger border-0 rounded-circle position-absolute top-0 end-0 m-2 p-2">                       X                    </button>                </div>            </div>        </div>        <!-- card end-->         ';         }         echo $card;     }else{         echo "<h5 class='text-muted text-center'>Cart is Empty</h5>";     }  }    public function removeItem():void    {        $itemId = $_POST['itemId'];        $cart = new \models\cart();        $cart->RemoveItemBaseOnId($itemId);    }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])) {    $cart = new \controllers\cartController();    switch ($_POST['action']) {        case 'addToCart':            $cart->store();            break;        case 'RemoveItem':            $cart->removeItem();            break;        default:            echo "Method not Allowed";            break;    }}