<?phpnamespace controllers;use models\users;require '../models/users.php';class clientController{    public function show():void    {        $client = new users();        $TableTr = '';        $data = $client->show();        if ($data){            foreach ($data as $clientList){                $TableTr .= '                    <tr>                          <th>'.$clientList['id'].'</th>                            <td>'.$clientList['Username'].'</td>                             <td>'.$clientList['address'].'</td>                          <td>'.$clientList['Email'].'</td>                   </tr>                ';            }        }else{            echo $TableTr .= '                <tr>                    <td colspan="4">No User Found</td>                </tr>            ';        }        echo $TableTr;    }}