<?phpnamespace models;use config\Connection;require_once '../config/Connection.php';class auth extends Connection{   public function registerUser(string $username, string $password,string $email, string $contact,string $address):void   {      $conn = $this->connect();         $query = "INSERT INTO client(Username, password, email, contact, Addess) VALUES(?,?,?,?,?)";         $stmt = $conn->prepare($query);         $stmt->bind_param('sssss', $username, $password, $email, $contact, $address);         if ($stmt->execute()) {              echo json_encode(['success'=> true,'message'=>'User registered successfully']);         } else {              echo "Error: " . $query . "<br>" . $this->connect()->error;         }         $stmt->close();         $this->connect()->close();   }   public function loginUser(string $username, string $password):void   {       $conn = $this->connect();       $query = "SELECT * FROM client WHERE Username = ?";       $stmt = $conn->prepare($query);         $stmt->bind_param('s', $username);            $stmt->execute();            $result = $stmt->get_result();            $user = $result->fetch_assoc();            if($user === null){                echo json_encode(['success'=> false,'message'=>'Account does not exist']);                return;            }            if (password_verify($password, $user['Password'])) {                echo json_encode(['success'=> true,'message'=>'User logged in successfully']);                session_start();                $_SESSION['user'] = $user['id'];                $_SESSION['Email'] = $user['Email'];                $_SESSION['contact'] = $user['contact'];                $_SESSION['address'] = $user['Addess'];                $_SESSION['username'] = $username;            } else {                echo json_encode(['success'=> false,'message'=>'Invalid username or password']);            }   }}