<?phpnamespace models;use config\Connection;require_once '../config/Connection.php';class category extends Connection{   public function store($categoryName):void   {       $conn = $this->Connect();       $query = "INSERT INTO category(categoryName)values (?)";       $stmt =$conn->prepare($query);       $stmt->bind_param('s',$categoryName);       if ($stmt->execute()){           echo json_encode(['success' => true, 'message' => 'Successfully Added New Category']);       }else{           echo json_encode(['success' => false, 'message' => 'Error'.$stmt->error]);       }       $stmt->close();   }   public function showAll($status):array   {       $conn = $this->Connect();       $query = "SELECT categoryName,status,cateId FROM category WHERE status = ?";       $stmt = $conn->prepare($query);       $stmt->bind_param('s',$status);       $stmt->execute();       $result = $stmt->get_result();       $data = [];       if ($result->num_rows > 0){           while ($row = $result->fetch_assoc()){               $data[] = $row;           }           $stmt->close();           return $data;       }       return [];   }}