<?phpnamespace models;use config\Connection;require_once '../config/Connection.php';class reservation extends Connection{  public function store(string $date, string $time, string $eventType, string $Name, string $contact, string $Address, int $clientId, string $proofPayment, string $Reference,string $email):void  {      $conn = $this->Connect();      $query = "INSERT INTO reservation(Date, time, eventType, Name, contact, Address, clientId, proofPayment, Reference,email) VALUES(?,?,?,?,?,?,?,?,?,?)";      $stmt = $conn->prepare($query);      $stmt->bind_param("ssssssisss", $date, $time, $eventType, $Name, $contact, $Address, $clientId, $proofPayment, $Reference,$email);      if ($stmt->execute()) {          echo json_encode(['success' => true, 'message' => 'Reservation has been successfully added']);      } else {          echo "Error: " . $query . "<br>" . $conn->error;      }        $stmt->close();        $conn->close();  }}