<?phpnamespace models;use config\Connection;require '../config/Connection.php';class users extends Connection{  public function show():array  {      $conn = $this->Connect();      $result = $conn->query("SELECT * FROM client");        $data = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $data[] = $row;            }            return $data;        }        return [];  }}